$database = "\\APPEDMDEV\RMS"$category = ""$filePath = "C:\DataFeeds\RMS\DBT\RMS"function CreateTSTemplate([string] $Database, [string] $Location, [string] $Category){	Import-Module 'C:\Windows\Microsoft.NET\assembly\GAC_MSIL\Xenomorph.TimeScape.PowerShell\v4.0_5.0.4000.0__2adfa95c2785233e\Xenomorph.TimeScape.PowerShell.dll'	$path = $Location 	# if category is specified, append it to name of template    if($Category -ne "") {$path = $path + "_" + $Category}	    # add .xml to filename    $path = $path + ".xml"    if($Category -ne "")    {        try        {            $Categories = Get-Categories -Database $Database            foreach($DBCategory in $Categories)            {                if($DBCategory -eq $Category)                {                    Copy-Database -Database $Database  -Target $path -TargetIsXML -Categories -Include $Category                    return                }            }            write-host "Error: The specified category does not exist in $Database"        }        catch         {            $ErrorMessage = $_.Exception.Message            write-host "Error: Unable to create the Database Template"            write-host $ErrorMessage            }    }    else     {        try        {            Copy-Database -Database $Database  -Target $path -TargetIsXML        }        catch        {            $ErrorMessage = $_.Exception.Message            write-host "Error: Unable to create the Database Template"            write-host $ErrorMessage            }    }}CreateTSTemplate -Database $database -Location $filePath -Category $category